import { Route,useParams  } from "react-router-dom";
import { Fragment } from "react";
import Comments from "../components/comments/Comments";

const Data = [
    { id: "q1", author: "Rosuljon", text: "Rosuljon is the best" },
    { id: "q2", author: "Gulhayo", text: "Gulhayo is the second best" },
  ];

const QuoteDetail = () =>{
    const param = useParams();
    const quote = Data.find(quote => quote.id === param.quoteId)
    return <Fragment>
        <h1>{param.quoteId}</h1>
        <Route path={`/quotes/${param.quoteId}/comments`}>
               <Comments />
            </Route>
    </Fragment>
}
export default QuoteDetail;
